mapper.select:
		select 
			count(*) as cnt
		 from tb_work_plan
		 where 1=1


		 		and idx=#{idx}


		 		and plan_type=#{plan_type}


		 		and plan_writer=#{plan_writer}


 			   	and (#{s_day} BETWEEN DATE_FORMAT(s_day, '%Y-%m-%d') and DATE_FORMAT(e_day, '%Y-%m-%d') 
 			   		or #{e_day} BETWEEN DATE_FORMAT(s_day, '%Y-%m-%d') and DATE_FORMAT(e_day, '%Y-%m-%d')
 			   		or DATE_FORMAT(s_day, '%Y-%m-%d') BETWEEN #{s_day} and #{e_day}
 					or DATE_FORMAT(e_day, '%Y-%m-%d') BETWEEN #{s_day} and #{e_day}
 				)

select w.idx, w.contract_name, w.plan_type, w.plan_key, w.plan_writer, w.plan_name, w.write_day, w.s_day, w.e_day, w.req_dept, w.per_dept, w.supervisor_id, w.worker_id, w.supervisor_name, w.worker_name, w.plan_contents, w.plan_note, w.app_turn, w.app_id, w.app_name, w.app_total, w.app_status, w.app_time from tb_work_plan w where 1=1
and idx=#{idx}
and w.plan_type=#{plan_type}
and plan_writer=#{plan_writer}
and (#{s_day} BETWEEN DATE_FORMAT(s_day, '%Y-%m-%d') and DATE_FORMAT(e_day, '%Y-%m-%d') or #{e_day} BETWEEN DATE_FORMAT(s_day, '%Y-%m-%d') and DATE_FORMAT(e_day, '%Y-%m-%d') or DATE_FORMAT(s_day, '%Y-%m-%d') BETWEEN #{s_day} and #{e_day} or DATE_FORMAT(e_day, '%Y-%m-%d') BETWEEN #{s_day} and #{e_day} )
order by idx desc limit #{sRow}, #{eRow}

select w.idx, w.contract_name, (select contract_name from tb_contract_manage where idx=w.contract_name) as contract_name_name, w.plan_type, (select type from tb_type_seq where idx=w.plan_type) as plan_type_name, w.plan_key, w.plan_writer, w.plan_name, w.write_day, w.s_day, w.e_day, w.req_dept, w.per_dept, w.supervisor_id, w.worker_id, w.supervisor_name, w.worker_name, w.plan_contents, w.plan_note, w.app_turn, w.app_id, w.app_name, w.app_total, w.app_status, w.app_time from tb_work_plan w where 1=1
and idx=#{idx}
and plan_writer=#{plan_writer}
and (#{s_day} BETWEEN DATE_FORMAT(s_day, '%Y-%m-%d') and DATE_FORMAT(e_day, '%Y-%m-%d') or #{e_day} BETWEEN DATE_FORMAT(s_day, '%Y-%m-%d') and DATE_FORMAT(e_day, '%Y-%m-%d') or DATE_FORMAT(s_day, '%Y-%m-%d') BETWEEN #{s_day} and #{e_day} or DATE_FORMAT(e_day, '%Y-%m-%d') BETWEEN #{s_day} and #{e_day} )
order by idx desc limit #{sRow}, #{eRow}
