mapper.select:
select 
	count(*) as cnt
 from tb_pwd_modify_history
 where 1=1


 		and idx=#{idx}



 		and user_id=#{user_id}


 		and muser_id=#{muser_id}

select m.idx, m.user_id, (select user_name from tb_user_info where user_id=#{user_id}) as user_name, m.muser_id, (select user_name from tb_user_info where user_id=#{user_id}) as muser_name, m.m_day from tb_pwd_modify_history m where 1=1
and idx=#{idx}
and user_id=#{user_id}
and muser_id=#{muser_id}
order by m.idx desc limit #{sRow}, #{eRow}

select m.idx, m.user_id, (select user_name from tb_user_info where user_id=#{user_id}) as user_name, m.muser_id, (select user_name from tb_user_info where user_id=#{user_id}) as muser_name, m.m_day from tb_pwd_modify_history m where 1=1
and idx=#{idx}
and user_id=#{user_id}
and muser_id=#{muser_id}
order by m.idx desc limit #{sRow}, #{eRow}
