mapper.select:
select 
	count(*) as cnt
 from tb_user_entrust
 where 1=1		

 		and user_id=#{user_id}



 		and user_id_entrust=#{user_id_entrust}



 		and status=#{status}

select u.idx, u.day, u.user_id, (select user_name from tb_user_info where u.user_id=user_id) as user_name, u.user_id_entrust, (select user_name from tb_user_info where u.user_id_entrust=user_id) as user_name_entrust, u.s_day, u.e_day, u.status from tb_user_entrust u where 1=1
and u.user_id=#{user_id}
and u.user_id_entrust=#{user_id_entrust}
and u.status=#{status}
order by u.idx desc limit #{sRow}, #{eRow}

mapper.update:
update tb_user_entrust set
	status='N'
where idx=#{idx}
